<?phpclass Avantio{    // avantio    private $avantio_credentials;    private $avantio_files;    private $avantio_credential = "servidor";    private $avantio_credential_local = "servidor";    // Files Downloaded    private $files_downloaded      = array();    private $files_downloaded_temp = array();    // report    private $last_insert_id_report = 0;    // post_id    private $post_id = 0;    # Objects Nuevo and KindAlquiler    private $status = "";    private $kindAlquiler = "";    # db wpdb connection    private $db = "";    private $dblocal = "";    public function __construct($param = null)    {        //echo "parametro: " . $param;        $this->initialize($param);    } // end function    /**     * @param null $param     */    private function initialize($param = null)    {        # conntect db        $this->connectDb();        # avantio credentials only for language        $this->setAvantioCredentials(null,"test");        # delete | descatalogado        //$this->delete_all();        # createPostsAndTaxonomies | descatalogado        //$this->createPostsAndTaxonomies();        # update or insert information        $this->update();    } // end function    private function connectDb()    {        # db        $connector = new DB();        $connector->setCredential($this->getAvantioCredential());        $this->db = $connector::getInstance();        # dblocal        $connector = new DBLocal();        $connector->setCredential($this->getAvantioCredentialLocal());        $this->dblocal = $connector::getInstance();    } // end function    private function update()    {        # acommodation        $this->updateAccomodation();        # entorno        $this->updateEntorno();        # picture        $this->updatePicture();        # description        $this->updateDescription();        # availabilityRates        //$this->updateAvalabilityRates();   } // end function    private function updateAccomodation()    {        $accomodation = new Accomodation();        $accomodation->setAvantioCredential($this->getAvantioCredential());        $accomodation->connectDb();        $accomodation->insertAccomodations($this->getAvantioCredentials());    } // end function    private function updateEntorno()    {        $entorno = new Entorno();        $entorno->setAvantioCredential($this->getAvantioCredential());        $entorno->connectDb();        $entorno->insertEntorno();    } // end function    private function updatePicture()    {        $picture = new Picture();        $picture->setAvantioCredential($this->getAvantioCredential());        $picture->connectDb();        $picture->insertPictures($this->getAvantioCredentials());    } // end function    private function updateDescription()    {        $description = new Description();        $description->setAvantioCredential($this->getAvantioCredential());        $description->connectDb();        $description->insertDescriptions($this->getAvantioCredentials());    } // end function    private function updateAvalabilityRates()    {        $availabilityRates = new AvailabilityRates();        $availabilityRates->setAvantioCredential($this->getAvantioCredential());        $availabilityRates->connectDb();        $availabilityRates->insertAccomodations($this->getAvantioCredentials());    } // end function    /**     * @return string     */    public function getAvantioCredential(): string    {        return $this->avantio_credential;    }    /**     * @param string $avantio_credential     */    public function setAvantioCredential(string $avantio_credential): void    {        $this->avantio_credential = $avantio_credential;    }    /* ********************************************* descatalogado ***************************************** */    private function createPostsAndTaxonomies()    {        # psot and taxonomies        $postAndTaxonomies = new CreatePostsAndTaxonomies();        $postAndTaxonomies->create_post_type_estate_property();        $postAndTaxonomies->create_taxonomy_property_category();        $postAndTaxonomies->create_taxonomy_property_action_category();        $postAndTaxonomies->create_taxonomy_property_city();        $postAndTaxonomies->create_taxonomy_property_area();        $postAndTaxonomies->create_taxonomy_property_features();        $postAndTaxonomies->create_taxonomy_property_extra_services();        $postAndTaxonomies->create_taxonomy_property_status();        $postAndTaxonomies->create_taxonomy_property_swimming_pool($this->avantio_credentials);        # insert features        // $features = new Feature();        //$features->createFeatures($this->avantio_credentials);    } // end function    private function print_xml_avantio($view_xml,$xml)    {        if ($view_xml)            print_r($xml);    } // end function    private function updateTaxonomies()    {        # create custom post type and taxonomies        $postAndTaxonomies = new CreatePostsAndTaxonomies();        $postAndTaxonomies->create_post_type_estate_property();        $postAndTaxonomies->create_taxonomy_property_category();        $postAndTaxonomies->create_taxonomy_property_action_category();        $postAndTaxonomies->create_taxonomy_property_city();        $postAndTaxonomies->create_taxonomy_property_area();        $postAndTaxonomies->create_taxonomy_property_features();        $postAndTaxonomies->create_taxonomy_property_extra_services();        $postAndTaxonomies->create_taxonomy_property_status();        //$postAndTaxonomies->create_taxonomy_property_swimming_pool($this->avantio_credentials);        //$postAndTaxonomies->insert_taxononomy_property_swimming_pool($this->avantio_credentials);        # insert features        $features = new Feature();        //$features->createFeatures($this->avantio_credentials);    } // end function    /**     * @param $xml     */    private function updateEstateProperty()    {        $estateProperty = new EstateProperty();        $estateProperty->insertEstateProperty($this->getAvantioCredentials());    } // end function    private function updateServices($xml)    {        $service = new Service();        $service->insertServices($xml,$this->getAvantioCredentials());        //$this->write_report_historico("services");    } // end function    private function updateGeographicAreas($xml)    {        $geographicArea = new GeographicArea();        $geographicArea->insertGeographicAreas($xml,$this->getAvantioCredentials());        //$this->write_report_historico("geographicareas");    } // end function    private function updateKinds($xml)    {        $kinds = new Kind();        $kinds->insertKinds($xml,$this->getAvantioCredentials());        //$this->write_report_historico("kinds");    } // end function    private function updateKindsAlquiler()    {        $this->kindsAlquiler = new KindAlquiler();        $this->kindsAlquiler->insertKinds($this->getAvantioCredentials());        //$this->write_report_historico("kinds");    } // end function    private function updateStatus()    {        $this->status = new Status();        $this->status->insertStatus($this->getAvantioCredentials());        //$this->write_report_historico("kinds");    } // end function    private function updateOcupationRules($xml,$ts,$ts_referencia)    {        $ocupationRules = new OcuppationRule();        $ocupationRules->insertOcuppationRules($xml,$this->getAvantioCredentials(),$ts,$ts_referencia);        //$this->write_report_historico("occupationalrules");    } // end function    private function updateAvalability($xml,$ts,$ts_referencia)    {        $availability = new AvailabilityRates();        $availability->insertAvailabilities($xml,$this->getAvantioCredentials(),$ts,$ts_referencia);        //$this->write_report_historico("availabilities");    } /