rt();        $sql="INSERT INTO avantio_report_historico (id,id_avantio_report,fichero) VALUES ('".$id."' , '" . $id_avantio_report . "', '".$db->escapeString($fichero)."'  ) ;";        if (!$db->query($sql)){            $error = $db->error();            echo "Error " . $error["code"] . " con la descripción " . $error["message"];            die();        }// end if    } // end function    /**     * @return array     */    public function getFilesDownloadedTemp()    {        return $this->files_downloaded_temp;    }    /**     * @param array $files_downloaded_temp     */    public function setFilesDownloadedTemp($files_downloaded_temp , $system)    {        if (!$files_downloaded_temp){            if ($system == "linux"){            }else if($system == "windows"){                $this->files_downloaded = array(                    "static" => Array(                        "geographicareas" => "C:\htdocs\avantio_cron_cli\app\Controllers/../xmldata/geographicareas.20210730115107.xml",                        "services"        => "C:\htdocs\avantio_cron_cli\app\Controllers/../xmldata/services.20210730115108.xml",                        "kinds"           => "C:\htdocs\avantio_cron_cli\app\Controllers/../xmldata/kinds.20210730115108.xml"                    ),                    "dynamic" => array(                        "accommodations" => "C:\htdocs\avantio_cron_cli\app\Controllers/../xmldata/accommodations.20210730115108.xml",                        //"descriptions"   => "C:\htdocs\avantio_cron_cli\app\Controllers/../xmldata/descriptions.20210711122105.xml",                        "descriptions"   => "C:\htdocs\avantio_cron_cli\app\Controllers/../xmldata/descriptions.20210711205936.xml"                    ),                    "prices" => array(                        "availabilities"    => "C:\htdocs\avantio_cron_cli\app\Controllers/../xmldata/availabilities.20210730115109.xml",                        "rates"             => "C:\