s->kindAlquiler[$lang];    }    /**     * @param string $kindAlquiler     */    public function setKindAlquiler($kindAlquiler)    {        $this->kindAlquiler = $kindAlquiler;    }    /**     * @param $xml     * @param $avantio_credentials     */    public function insertPictures($avantio_credentials)    {        //echo "site_url: " .get_option('siteurl'). "<br>";        # vectors        $post_vector = array();        # counters        $counter_property = 0;        $counter_property_create = 0;        $id_anterior = 0;        # get accommodations        $xml =  $this->getAcommodationById($avantio_credentials);        //p_($xml);        //die();        //p_($xml[0]);        //p_($xml[1]);        foreach ($xml as $accommodation) {            # id, language, text_title, text_referencia, text_userid, text_company, number_companyid, text_numero_registro_turistico, avantio_occupation_rules, avantio_pricemodifiers, avantio_gallery, dynamic_taxonomy, dynamic_geocountry, dynamic_georegion, dynamic_geocity, dynamic_geolocality, dynamic_geodistrict, text_geo_cp, text_geo_tipo_calle, text_geo_calle, text_geo_numero, text_geo_bloque, text_geo_puerta, text_geo_piso, text_geo_latitud, text_geo_longitud, number_geo_zoom, number_unidades, number_habitaciones, number_camas_doble, number_camas_individual, number_sofas_cama, number_sofas_cama_doble, number_literas, number_dormitorios_personal, number_camas_supletorias, number_aseos, number_banyos_banyera, number_banyos_ducha, number_metros_cuadrados, number_metros_cuadrados_utiles, number_metros_cuadrados_construidos, number_metros_cuadrados_terraza, number_capacidad_maxima, number_capacidad_minima, number_capacidad_sin_suplemento, number_cocinas, number_fun, text_cocina_clase, text_cocina_tipo, text_orientacion, checkbox_grups, checkbox_aparcamiento, number_plazas_aparcamiento, checkbox_piscina, text_dimensiones_piscina, text_tipo_piscina, checkbox_piscina_interior_climatizada, checkbox_piscina_c