area_description, text_page_title, auto_slug, text_slug, text_meta_keywords, text_meta_robots, text_meta_description, textarea_scripts_header, textarea_scripts_body, status, position, id_avantio, taxonomy, country, region, city, locality, district            $id = (int)$accommodation->id;            $text_title = (string)$accommodation->text_title;            $descripcion = (string)$accommodation->text_title;            $lang = (string)$accommodation->language;            if ($id_anterior != $id) {                $id_anterior = $id;                $counter_property++;            }// end if            if($counter_property <= 1) {                # insert                if (!get_post_status($id)) {                    # translations                    $my_post_translation = pll_get_post_translations($id);                    # images                    $images = $this->getImages($id);                    foreach($my_post_translation as $key_lang => $id_post){                        $counter_image = 0;                        foreach ($images as $image) {                            if($counter_image < 2){                                //echo "imagenes: " .$image->uri_900x600. "<br>";                                $this->insert_property_image((string)$image->uri_900x600, $my_post_translation[$key_lang]);                            }// end if                            $counter_image++;                        } // end foreach                    }// end foreach                # update                }else{                    # translations                    $my_post_translation = pll_get_post_translations($id);                    # images                    $images = $this->getImages($id);                    foreach($my_post_translation as $key_lang => $id_post){                        $counter_image = 0;                        foreach ($images as $image) {                            if($counter_image < 3){                               //echo "imagenes: " .$image->uri_900x600. "<br>";                                $this->insert_property_image((string)$image->uri_900x600, $my_post_translation[$key_lang]);                            }// end if                            $counter_image++;                        } // end foreach                    }// end foreach                } // check if post exist            } // end if counter property        } // end foreach accomoddation    } // end function    private function create_post($id, $title , $description , $action){        $current_user  =   wp_get_current_user();        $new_user_id   = $current_user->ID;        $new_status = 'publish';        $post = "";        if($action == "insert"){            $post = array(                'import_id'    => $id,                'post_title'	=> sanitize_title($title),                'post_status'	=> $new_status,                'post_type'     => 'estate_property',                'post_author'   => $new_user_id ,                'post_content'  => $description            );        }else if($action == "update" || $action == "test" || $action == "new"){            $post = array(                'ID'            => $id,                'post_title'	=> sanitize_title($title),                'post_status'	=> $new_status,                'post_type'     => 'estate_property',                'post_author'   => $new_user_id ,                'post_content'  => $description            );        }else if($action == "nada"){            $post = array(                'post_title'	=> sanitize_title($title),                'post_status'	=> $new_status,                'post_type'     => 'estate_property',                'post_author'   => $new_user_id ,                'post_content'  => $description            );        }// end if        //my_print($post);        return $post;    }    /**     * @return mixed     */    public function getPostId()    {        return $this->post_id;    }    /**     * @param mixed $post_id     */    public function setPostId($post_id)    {        $this->post_id = $post_id;    }    private function getImages($acommodation_id)    {        $sql = "select * from avantio_accomodations_pictures where avantio_accomodations = '".$acommodation_id."' order by id asc";        $acommodations = $this->db->get_results($sql);        return  ($acommodations) ? $acommodations : false;    } // end function    //add_action( 'init', 'download_photos' );    function download_photos(){        $file_url = "http://img.crs.itsolutions.es/fotos/2/16296628291f9a869e78ce86803e45e31a397d0922/16296628315df9646ccb34fe99bc422c9273fb12c6.jpg";        $post_id  = 362599;        insert_property_image($file_url, $post_id);    }    function insert_property_image($file_url, $post_id){        require_once(ABSPATH . 'wp-load.php');        require_once(ABSPATH . 'wp-admin/includes/image.php');        global $wpdb;        if(!$post_id) {            return false;        }        // directory to import to        $artDir = 'wp-content/uploads/importedmedia/';        // if the directory doesn't exist, create it        if(!file_exists(ABSPATH.$artDir)) {            mkdir(ABSPATH.$artDir);        }        // rename the file... alternatively, you could explode on "/" and keep the original file name        $name_file = explode("/", $file_url);        $name_file = $name_file[count($name_file)-1];        $name_file = explode(".", $name_file);        $name_file = $name_file[0];        $name_file = substr($name_file,0,strlen($name_file)-1);        $ext = array_pop(explode(".", $file_url));        //$new_filename = 'blogmedia-'.$post_id.".".$ext; //if your post has multiple files, you may need to add a random number to the file name to prevent overwrites        $new_filename = 'blogmedia-'.$name_file.".".$ext; //if your post has multiple files, you may need to add a random number to the file name to prevent overwrites                    //echo "file_url: " .$file_url. "<br>";        // rename the file... alternatively, you could explode on "/" and keep the original file name        $ext = array_pop(explode(".", $file_url));        $new_filename = 'blogmedia-'.$post_id.".".$ext; //if your post has multiple files, you may need to add a random number to the file name to prevent overwrites        if (@fclose(@fopen($file_url, "r"))) { //make sure the file actually exists            //echo "path".ABSPATH.$artDir.$new_filename."<br>";            copy($file_url, ABSPATH.$artDir.$new_filename);            $siteurl = get_option('siteurl');            $file_info = getimagesize(ABSPATH.$artDir.$new_filename);            //create an array of attachment data to insert into wp_posts table            $artdata = array();            $artdata = array(                'post_author' => 1,                'post_date' => current_time('mysql'),                'post_date_gmt' => current_time('mysql'),                'post_title' => $new_filename,                'post_status' => 'inherit',                'comment_status' => 'closed',                'ping_status' => 'closed',                'post_name' => sanitize_title_with_dashes(str_replace("_", "-", $new_filename)),                'post_modified' => current_time('mysql'),                'post_modified_gmt' => current_time('mysql'),                'post_parent' => $post_id,                'post_type' => 'attachment',                'guid' => $siteurl.'/'.$artDir.$new_filename,                'post_mime_type' => $file_info['mime'],                'post_excerpt' => '',                'post_content' => ''            );            $uploads = wp_upload_dir();            $save_path = $uploads['basedir'].'/importedmedia/'.$new_filename;            // insert the database record            $attach_id = wp_insert_attachment( $artdata, $save_path, $post_id );            // generate metadata and thumbnails            if ($attach_data = wp_generate_attachment_metadata( $attach_id, $save_path)) {                wp_update_attachment_metadata($attach_id, $attach_data);            }            // optional make it the featured image of the post it's attached to            $rows_affected = $wpdb->insert($wpdb->prefix.'postmeta', array('post_id' => $post_id, 'meta_key' => '_thumbnail_id', 'meta_value' => $attach_id));            set_post_thumbnail( $post_id, $attach_id );        } else {            return false;        }        return true;    }    /**     * @return bool     */    private function getAcommodationById($avantio_credentials)    {        $sql = "select ac.*from avantio_accomodations as ac where language = 'es' order by ac.id asc";        $acommodations = $this->db->get_results($sql);        return  ($acommodations) ? $acommodations : false;    } // end function}// end class?>